/**
 * @param {number[]} nums
 * @return {number[][]}
 */
 const threeSum = (nums) => {

    if( nums.length < 3 ) return [];

    nums.sort();
    //console.log(nums);
    const results = [];
    let memo = {};

    // time complexity = O(n) * O(n)
    for (let i=0; i<nums.length; i++) {
        if ( nums[i] === nums[i+1] ) continue;
        let targetsum = 0-nums[i];
        let twosumResult = twosum(nums, targetsum, i);
        for( let triplet of twosumResult ) {
            results.push( triplet );
        }
    }
    globalCache = {};

    return results;
 };

 const twosum = (nums, targetsum, i) =>{

    let memo = {};
    const results = [];

    // * time comp = O(n)
    for( let j=0; j<nums.length; j++) {
        if( j === i ) continue;
        let remainder = targetsum - nums[j];
        if( remainder in memo ) {
           let key = [nums[i], nums[memo[remainder]], nums[j]];
           key.sort();
           if( key in globalCache ) continue;
           globalCache[key]=1;
           results.push(key);
        }
        memo[nums[j]] = j;
    }

    return results;
};

const threeSum2 = (nums) => {

    if( nums.length < 3) return [];

    const solutions = [];

    nums.sort(function(a,b) {return a-b;});
    // time complexity = O(n)
    for( let i=0; i<nums.length-2; i++ ) {
        if( i===0 || (i>0 && nums[i] !== nums[i-1])) {
            var lo = i+1;
            var hi = nums.length - 1;
            var sum = -nums[i];
            while( lo<hi ) {
                if( nums[lo] + nums[hi] === sum ) {
                    solutions.push([nums[i], nums[lo], nums[hi]]);
                    while ( lo<hi && nums[lo] === nums[lo+1] ) lo++;
                    while ( lo<hi && nums[hi] === nums[hi-1] ) hi--;
                    lo++; hi--;
                } else if (nums[lo] + nums[hi] > sum) {
                    hi--;
                } else {
                    lo++
                }
            }
        }
    }
    return solutions;
};

var globalCache = {};

